#!/usr/bin/env bash
cd "$(dirname "$0")"

if ! type xprop > /dev/null 2>&1; then
    echo "(!) The xprop command was not found. For details on installing prerequistes, see"
    echo "    https://aka.ms/vsls-docs/linux-prerequisites"
    exit 127
fi

if ! type desktop-file-install > /dev/null 2>&1; then
    echo "(!) The desktop-file-install command was not found. For details on installing"
    echo "    prerequistes, see https://aka.ms/vsls-docs/linux-prerequisites"
    exit 127
fi

exit 0