const path = require('path');
const fs = require('fs');
const eol = require('eol');

const LAUNCHER_FILE_PATH = path.join(__dirname, '..', 'vsls-launcher');
const launcherContents = fs.readFileSync(LAUNCHER_FILE_PATH).toString();

if (!launcherContents) {
    throw new Error('Failed to read launcher contents');
}

fs.writeFileSync(LAUNCHER_FILE_PATH, eol.lf(launcherContents));

console.log('Line breaks in the launcher file updated to LF.\n');